function GuiService() {

  function getSmallSummary(item) {
    var html = '<div class="small-summary">';
    if (item['name']) {
      html += "<b>" + item['name'] + "</b>";
    }
    if (item['description']) {
      html += "<br>" + item['description'];
    }
    html += '</div>';
    return html;
  }

  this.getSmallSummary = getSmallSummary;
}

module.exports = new GuiService;
