function GuiService() {

  function getSmallSummary(item) {
    var html = '<div class="small-summary">';
    if (item['name']) {
      html += "<b>" + item['name'] + "</b>";
    }
    if (item['description']) {
      html += "<br>" + item['description'];
    }
    html += '</div>';
    return html;
  }



  function getCreateNewPopup(lat, lon, callback) {
    var html = '<div class="create-new">';
    html += '<div class="input-label">Name</div>';
    html += '<input type="text" class="name-input" />';
    html += '<div class="input-label">Description</div>';
    html += '<textarea rows="4" class="description-input"></textarea>';
    html += '<div class="input-label">Tags (separate by comma)</div>';
    html += '<input type="text" class="tags-input" />';
    html += '<div class="location-field">Location: ' + lat.toFixed(4) +', ' + lon.toFixed(4) + '</div>';
    html += '<div class="input-controls clearfix">';

    var cancel_id = "cancel-" + lat + "-" + lon;
    var accept_id = "accept-" + lat + "-" + lon;

    html += '<div class="input-cancel"><img src="../img/cancel.svg" /></div>';
    html += '<div class="input-accept"><img src="../img/accept.svg" /></div>';
    html += '</div>';
    return html;
  }

  this.getSmallSummary = getSmallSummary;
  this.getCreateNewPopup = getCreateNewPopup;
}

module.exports = new GuiService;
